SetDrive	proc near		; ...
		les	di, ds:start_bds ; Point es:di to first	bds

X_Scan_Loop:				; ...
		cmp	es:[di+5], al	; logical drive	number (BDS.drivelet)
		jz	short X_SetDrv
		les	di, es:[di]	; [es:di+BDS.link] ; Go	to next	bds
		cmp	di, 0FFFFh
		jnz	short X_Scan_Loop
		stc

X_SetDrv:				; ...
		retn
SetDrive	endp

