		push	es		; preserve current bds
		push	di
		push	ds		; point	es to Bios_Data	(BIOSDATA)
		pop	es
		mov	di, offset tmp_vid ; "NO NAME	 "
		mov	cx, 11		; VOLID_SIZ-1
					; length of string minus nul
		rep movsb
		xor	al, al
		stosb			; null terminate
		xor	si, si
		pop	di		; restore current bds
		pop	es

