		int	10h		; (whoops, this	enables	ints)
					; - VIDEO - WRITE CHARACTER AND	ADVANCE	CURSOR (TTY WRITE)
					; AL = character, BH = display page (alpha modes)
					; BL = foreground color	(graphics modes)
		jmp	short fatal_loop
; ---------------------------------------------------------------------------

fatal_done:				; ...
		jmp	short fatal_done
longpath	endp

