		mov	word ptr ds:1, 0 ; [ARENA.OWNER],0 ; free
		sub	cx, bx		; size of the new block
		mov	ds:3, cx	; [ARENA.SIZE],cx ; store it in	the arena
		mov	ax, es		; return seg to	the caller
		sub	ax, 3		; Take care ORG	30h of BIOS code
		pop	dx
