		dw 0			; interrupt routine offset
		db 1			; number of units
		db 7 dup(0)		; reserved area
VDiskSig1	db 'VDISK'              ; ...
aV3_3		db '  V3.3'             ; vdisk label ; VLEN1 equ ($-VDiskSig1)
		db 15 dup(0)		; pad
