		xor	ax, ax
		mov	ds, ax
		assume ds:nothing
		mov	ds, ds:off_4D+1	; [13h*4+2]
		assume ds:nothing
		mov	si, 12h		; VDiskSig1-StartVDHead
		mov	cx, 5		; VLEN1
