stackinit	proc near		; ...
		push	ax		; ibmstack initialization routine
					; in - cs, ds -> sysinitseg,
					;      es -> relocated stack code & data.
		push	ds
		push	es
		push	bx
		push	cx
		push	dx
		push	di
		push	si
		push	bp
		mov	ax, cs:stack_count
		mov	es:stackcount, ax
