setdevmark	proc near		; ...
		push	es		; set the devmark for mem command.
					; in:
					;   [memhi] - the address to place devmark
					;   [memlo] = 0
					;   al = id for	devmark_id
					; out:
					;   devmark established.
					;   the	address	saved in cs:[devmark_addr]
					;   [memhi] increase by	1.
		push	cx
		mov	cx, cs:memhi
		mov	cs:devmark_addr, cx
		mov	es, cx
		mov	es:0, al
		inc	cx
		mov	es:1, cx
		pop	cx
		pop	es
		inc	cs:memhi
		retn
setdevmark	endp

