showlinenum	proc near		; ...
		push	es
		push	ds
		push	di
		push	cs
		pop	es
		assume es:SYSINIT
		push	cs
		pop	ds
		mov	di, (offset showcount+4) ;
					; di ->	the least significant decimal field.
		mov	cx, 10
		mov	ax, cs:linecount

sln_loop:				; ...
		cmp	ax, 10		; < 10 ?
		jb	short sln_last	; yes
		xor	dx, dx
		div	cx		; cx = 10
		or	dl, 30h		; convert to ascii numeric char	("0" to	"9")
		mov	[di], dl
		dec	di
		jmp	short sln_loop
; ---------------------------------------------------------------------------

sln_last:				; ...
		or	al, 30h		; convert to ascii numeric char	("0" to	"9")
		mov	[di], al
		mov	dx, di
		call	sub_9072	; show it
