		push	ds
		mov	bx, 40h		; ROMBIOS data area segment
		mov	ds, bx
		assume ds:nothing
		mov	bx, ds:13h	; memory size (KB)
		mov	cs:OldInt12Mem,	bx
		mov	cl, 6		; 16*64	= 1024
		shr	ax, cl		; convert paragraphs to	kilobyte
		mov	ds:13h,	ax
		mov	cs:Int12Lied, 0FFh ; mark that we are lying
		pop	ds
		assume ds:nothing
		retn
