		jz	short timx	; yes
					; no, we weren't
		push	ds
		mov	ax, 40h
		mov	ds, ax
		assume ds:nothing
		mov	ax, cs:OldInt12Mem
		mov	ds:13h,	ax	; restore INT 12h memory
		pop	ds
		assume ds:nothing

timx:					; ...
		retn
TrueInt12Mem	endp


