		pop	es
		assume es:SYSINIT
		xor	ax, ax		; 0
		mov	es:fUmbTiny, al	; Shrink UMBs? (made 1 if /S given)
		mov	es:fInHigh, al	; Set to 1 when	DH/LH has been called
		mov	es:SegLoad, ax	; Load Address (seg), used for DH only
		mov	es:UmbLoad, 0FFh ; UNSPECIFIED
