		mov	es:fm_argc, al	; Start	with zero args having been read
		cld
		mov	cx, 16		; MAXUMB
		mov	di, offset UmbUsed ;
					; For each entry on the	UmbUsed	array,
		rep stosb		; Store	0
		mov	cx, 16		; MAXUMB
		mov	di, offset UmbSize ; Okay...
					; for each entry on the	UmbSize	array,
		rep stosw		; Store	0
		pop	es
		assume es:nothing
		pop	di
		pop	cx
		pop	ax
		retn
