					; block	for the	next arena.
		mov	byte ptr es:0, 4Dh ; 'M' ; [es:ARENA.SIGNATURE],arena_signature_normal
		mov	cx, ax		; cx = top of prev block-1
		inc	ax
		sub	ax, bx		; ax = top of prev block - seg.	addr of	new block
		neg	ax
