  8613                                  	; 15/12/2022
  8614 00000F88 740D                    	jz	short siv_2
  8615                                  siv_1:	
  8616 00000F8A 36803E[8500]00          	cmp	byte [ss:A20OFF_COUNT],0 ; Q: is count 0
  8617 00000F90 7505                    	jnz	short siv_2		 ; N: done 
  8618                                  	; 20/09/2023
  8619 00000F92 36FE06[8500]            	inc	byte [ss:A20OFF_COUNT]
