 31286                                  	JNZ	short outrange2		    ;AN000;;LB. no
 31287                                  	MOV	AX,[ss:HIGH_SECTOR]	    ;AN000;;LB. subtract sector num from
 31288                                  	MOV	CX,DX			    ;AN000;;LB. secondary starting sector
 31289                                  	SUB	CX,[ss:CurSC_SECTOR]        ;AN000;;LB. number
 31290                                  	SBB	AX,[ss:CurSC_SECTOR+2]      ;AN000;;LB.
 31291                                  	; 24/09/2023
 31292                                  	;CMP	AX,0			    ;AN000;;LB. greater than 64K
 31293                                  	JNZ	short outrange2		    ;AN000;;LB. yes
 31294                                  	CMP	CX,[ss:SC_CACHE_COUNT]	    ;AN000;;LB. greater than SC count
 31295                                  	JAE	short outrange2		    ;AN000;;LB. yes
 31296                                  	CLC				    ;AN000;;LB. clear carry
 31297                                  	;JMP	short inexit		    ;AN000;;LB. in SC
 31298                                  	; 16/12/2022
 31299                                  	retn	; 30/04/2019
