 31308                                  ;Break	<INVALIDATE_SC - invalide secondary cache>
 31309                                  ;---------------------------------------------------------------------------
 31310                                  ;
 31311                                  ; Procedure Name : Invalidate_Sc
 31312                                  ;
 31313                                  ; Inputs:  [SC_DRIVE]= requesting drive
 31314                                  ;	   [CURSC_DRIVE]= current SC drive
 31315                                  ;	   [CURSC_SECTOR]= starting scetor # of SC
 31316                                  ;	   [SC_CACHE_COUNT]= SC count
 31317                                  ;	   [SC_STATUS]= SC status word
 31318                                  ;	   [HIGH_SECTOR]:DX= sector number
 31319                                  ;
 31320                                  ; Function:
 31321                                  ;	invalidate secondary cache if in there
 31322                                  ; Output:
 31323                                  ;	[SC_STATUS] is updated
 31324                                  ;---------------------------------------------------------------------------
 31325                                  
 31326                                  INVALIDATE_SC:
 31327                                  	; SS override for all variables used
 31328                                  
 31329                                  	CALL	IN_SC			    ;AN000;;LB. in secondary cache
 31330                                  	JC	short outrange		    ;AN000;;LB. no
 31331                                  	MOV	AX,1			    ;AN000;;LB. invalidate the sector
 31332                                  	SHL	AX,CL			    ;AN000;;LB. in the secondary cache
 31333                                  	NOT	AX			    ;AN000;;LB.
 31334                                  	AND	[ss:SC_STATUS],AX	    ;AN000;;LB. save the status
 31335                                  outrange:				    ;AN000;;LB.
 31336                                  	retn				    ;AN000;;LB.
 31337                                  
 31338                                  ; DOSCODE:87A5h (MSDOS 6.21, MSDOS.SYS)
 31339                                  ; 22/11/2022
 31340                                  ; DOSCODE:876Ah (MSDOS 5.0, MSDOS.SYS)
 31341                                  
 31342                                  ;Break	<VIRREAD- virtually read data into buffer>
 31343                                  ;--------------------------------------------------------------------------
 31344                                  ;
 31345                                  ; Procedure Name : SC_FLAG
 31346                                  ;
 31347                                  ; Inputs:  SC_FLAG = 0, no sectors were read into SC
 31348                                  ;		     1, continuous sectors were read into SC
 31349                                  ; Function:
 31350                                  ;	   Move data from SC to buffer
 31351                                  ; Output:
 31352                                  ;	 carry clear, data is moved to buffer
 31353                                  ;	 carry set, bad sector or exceeds maximum sector
 31354                                  ;	   SC_FLAG =0
 31355                                  ;	   CALLSCNT=1
 31356                                  ;	   SC_STATUS= -1 if succeeded
 31357                                  ;     
 31358                                  ;		       0 if failed
 31359                                  ;--------------------------------------------------------------------------
 31360                                  
 31361                                  VIRREAD:
 31362                                  	; SS override for all variables used
 31363                                  
 31364                                  	CMP	byte [ss:SC_FLAG],0	    ;AN000;;LB. from SC fill
 31365                                  	JZ	short sc2end		    ;AN000;;LB. no
 31366                                  	MOV	AX,[ss:TEMP_VAR2]	    ;AN000;;LB. restore buffer addr
 31367                                  	MOV	[ss:CALLXAD+2],AX	    ;AN000;;LB.
 31368                                  	MOV	AX,[ss:TEMP_VAR]	    ;AN000;;LB.
 31369                                  	MOV	[ss:CALLXAD],AX		    ;AN000;;LB.
 31370                                  	MOV	byte [ss:SC_FLAG],0	    ;AN000;;LB. reset sc_flag
 31371                                  	MOV	word [ss:CALLSCNT],1	    ;AN000;;LB. one sector transferred
 31372                                  
 31373                                  	;TEST	word [SS:DEVCALL_REQSTAT],STERR ;AN000;;LB. error?
 31374                                  	test	byte [ss:DEVCALL_REQSTAT+1],(STERR>>8) ; 80h
 31375                                  	JNZ	short scerror 		    ;AN000;;LB. yes
 31376                                  	PUSH	DS			    ;AN000;;LB.
 31377                                  	PUSH	SI			    ;AN000;;LB.
 31378                                  	PUSH	ES			    ;AN000;;LB.
 31379                                  	PUSH	DI			    ;AN000;;LB.
 31380                                  	PUSH	DX			    ;AN000;;LB.
 31381                                  	PUSH	CX			    ;AN000;;LB.
 31382                                  	XOR	CX,CX			    ;AN000;;LB. we want first sector in SC
 31383                                  	CALL	SC2BUF2 		    ;AN000;;LB. move data from SC to buf
 31384                                  	POP	CX
 31385                                  	POP	DX			    ;AN000;;LB.
 31386                                  	POP	DI			    ;AN000;;LB.
 31387                                  	POP	ES			    ;AN000;;LB.
 31388                                  	POP	SI			    ;AN000;;LB.
 31389                                  	POP	DS			    ;AN000;;LB.
 31390                                  	JMP	SHORT sc2end		    ;AN000;;LB. return
 31391                                  scerror:				    ;AN000;
 31392                                  	MOV	word [ss:CALLSCNT],1	    ;AN000;;LB. reset sector count to 1
 31393                                  	MOV	word [ss:SC_STATUS],0	    ;AN000;;LB. invalidate all SC sectors
 31394                                  	MOV	byte [ss:CurSC_DRIVE],-1    ;AN000;;LB. invalidate drive
 31395                                  	STC				    ;AN000;;LB. carry set
 31396                                  	retn				    ;AN000;;LB.
 31397                                  sc2end: 				    ;AN000;
 31398                                  	CLC				    ;AN000;;LB. carry clear
 31399                                  	retn				    ;AN000;;LB.
