 31406                                  ;Break	<SC2BUF- move data from SC to buffer>
 31407                                  ;----------------------------------------------------------------------------
 31408                                  ;
 31409                                  ; Procedure Name : SC2BUF
 31410                                  ;
 31411                                  ; Inputs:  [SC_STATUS] = SC validity status
 31412                                  ;	   [SC_SECTOR_SIZE] = request sector size
 31413                                  ;	   [SC_CACHE_PTR] = pointer to SC
 31414                                  ; Function:
 31415                                  ;	   Move data from SC to buffer
 31416                                  ; Output:
 31417                                  ;	   carry clear, in SC  and data is moved
 31418                                  ;	   carry set, not in SC and data is not moved
 31419                                  ;---------------------------------------------------------------------------
 31420                                  
 31421                                  SC2BUF:
 31422                                  	; SS override for all variables used
 31423                                  	CALL	IN_SC			    ;AN000;LB. in secondary cache
 31424                                  	;JC	short noSC		    ;AN000;LB. no
 31425                                  	; 24/09/2023
 31426                                  	jc	short sexit
 31427                                  	MOV	AX,1			    ;AN000;LB. check if valid sector
 31428                                  	SHL	AX,CL			    ;AN000;LB. in the secondary cache
 31429                                  	TEST	[ss:SC_STATUS],AX	    ;AN000;LB.
 31430                                  	JZ	short noSC		    ;AN000;LB. invalid
 31431                                  ;entry SC2BUF2
 31432                                  SC2BUF2:				    ;AN000;
 31433                                  	;MOV	AX,CX			    ;AN000;LB. times index with
 31434                                  	;MUL	word [ss:SC_SECTOR_SIZE]    ;AN000;LB. sector size
 31435                                  	; 24/09/2023
 31436                                  	mov	ax,[ss:SC_SECTOR_SIZE]
 31437                                  	xchg	ax,cx ; cx = [ss:SC_SECTOR_SIZE]
 31438                                  	mul	cx
 31439                                  	ADD	AX,[ss:SC_CACHE_PTR]	    ;AN000;LB. add SC starting addr
 31440                                  	ADC	DX,[ss:SC_CACHE_PTR+2]	    ;AN000;LB.
 31441                                  	MOV	DS,DX			    ;AN000;LB. DS:SI-> SC sector addr
 31442                                  	MOV	SI,AX			    ;AN000;LB.
 31443                                  	MOV	ES,[ss:CALLXAD+2]		    ;AN000;LB. ES:DI-> buffer addr
 31444                                  	MOV	DI,[ss:CALLXAD]		    ;AN000;LB.
 31445                                  	; 24/09/2023
 31446                                  	;MOV	CX,[ss:SC_SECTOR_SIZE]	    ;AN000;LB. count= sector size
 31447                                  	SHR	CX,1			    ;AN000;LB. may use DWORD move for 386
 31448                                  ;entry MOVWORDS
 31449                                  MOVWORDS:				    ;AN000;
 31450                                  	CMP	byte [ss:DDMOVE],0	    ;AN000;LB. 386 ?
 31451                                  	JZ	short nodd		    ;AN000;LB. no
 31452                                  	SHR	CX,1			    ;AN000;LB. words/2
 31453                                  	DB	66H			    ;AN000;LB. use double word move
 31454                                  nodd:
 31455                                  	REP	MOVSW			    ;AN000;LB. move to buffer
 31456                                  	CLC				    ;AN000;LB. clear carry
 31457                                  	retn				    ;AN000;LB. exit
 31458                                  noSC:					    ;AN000;
 31459                                  	STC				    ;AN000;LB. set carry
 31460                                  sexit:					    ;AN000;
 31461                                  	retn				    ;AN000;LB.
 31462                                  
 31463                                  %endif	; SC
