 32679                                  ;
 32680                                  ; Procedure Name : CHECK_VIRT_OPEN
 32681                                  ;
 32682                                  ; CHECK_VIRT_OPEN checks to see if we had performed a "virtual open" (by
 32683                                  ; examining the flag [VIRTUAL_OPEN] to see if it is 1). If we did, then
 32684                                  ; it calls Dev_Close_SFT to decrement the ref. count. It also resets the
 32685                                  ; flag [VIRTUAL_OPEN].
 32686                                  ; No registers affected (including flags).
 32687                                  ; On input, [THISSFT] points to current SFT.
 32688                                  ;
 32689                                  ;---------------------------------------------------------------------------
 32690                                  
 32691                                  CHECK_VIRT_OPEN:
 32692                                  	PUSH	AX
 32693                                  	lahf			; preserve flags
 32694                                  	CMP	byte [VIRTUAL_OPEN],0
 32695                                  	JZ	short ALL_CLOSED
 32696                                  	mov	byte [VIRTUAL_OPEN],0 ; reset flag
 32697                                  	push	es
 32698                                  	push	di
 32699                                  	LES	DI,[THISSFT]
 32700                                  	call	DEV_CLOSE_SFT
 32701                                  	pop	di
 32702                                  	pop	es
 32703                                  
 32704                                  ALL_CLOSED:
 32705                                  	sahf			; restore flags
 32706                                  	POP	AX
 32707                                  	retn
 32708                                  
 32709                                  %endif
