 34821                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34822                                  ;	      ; ^E and ^D
 34823                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34824                                  ;	      ; ^G and ^F
 34825                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34826                                  ;	      ; TAB and BS
 34827                                  ;	 db   LOW ((NOT FFCB+FCHK+FDELIM+FSPCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34828                                  ;	      ; ^K and ^J
 34829                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34830                                  ;	      ; ^M and ^L
 34831                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34832                                  ;	      ; ^O and ^N
 34833                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34834                                  ;	      ; ^Q and ^P
 34835                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34836                                  ;	      ; ^S and ^R
 34837                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34838                                  ;	      ; ^U and ^T
 34839                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34840                                  ;	      ; ^W and ^V
 34841                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34842                                  ;	      ; ^Y and ^X
 34843                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34844                                  ;	      ; ESC and ^Z
 34845                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34846                                  ;	      ; ^] and ^;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34848                                  ;	      ; ^_ and ^^
 34849                                  ;	 db   LOW ((NOT FFCB+FCHK) SHL 4) OR LOW (NOT FFCB+FCHK) AND 0Fh
 34850                                  ;	      ; ! and SPACE
 34851                                  ;	 db   LOW (NOT FCHK+FDELIM+FSPCHK)
 34852                                  ;	      ; # and "
 34853                                  ;	 db   LOW (NOT FFCB+FCHK)
 34854                                  ;	      ; $ - )
 34855                                  ;	 db   3 dup (0FFh)
 34856                                  ;	      ; + and *
 34857                                  ;	 db   LOW ((NOT FFCB+FCHK+FDELIM) SHL 4) OR 0Fh
