 39869                                  ;	EXEC.ASM - EXEC System Call
 39870                                  ;
 39871                                  ;
 39872                                  ; Assembler usage:
 39873                                  ;	    lds     DX, Name
 39874                                  ;	    les     BX, Blk
 39875                                  ;	    mov     AH, Exec
 39876                                  ;	    mov     AL, FUNC
 39877                                  ;	    int     INT_COMMAND
 39878                                  ;
 39879                                  ;	AL  Function
 39880                                  ;	--  --------
 39881                                  ;	 0  Load and execute the program.
 39882                                  ;	 1  Load, create the program header but do not
 39883                                  ;	    begin execution.
 39884                                  ;	 3  Load overlay. No header created.
 39885                                  ;
 39886                                  ;	    AL = 0 -> load/execute program
 39887                                  ;
 39888                                  ;	    +---------------------------+
 39889                                  ;	    | WORD segment address of	|
 39890                                  ;	    | environment.		|
 39891                                  ;	    +---------------------------+
 39892                                  ;	    | DWORD pointer to ASCIZ	|
 39893                                  ;	    | command line at 80h	|
 39894                                  ;	    +---------------------------+
 39895                                  ;	    | DWORD pointer to default	|
 39896                                  ;	    | FCB to be passed at 5Ch	|
 39897                                  ;	    +---------------------------+
 39898                                  ;	    | DWORD pointer to default	|
 39899                                  ;	    | FCB to be passed at 6Ch	|
 39900                                  ;	    +---------------------------+
 39901                                  ;
 39902                                  ;	    AL = 1 -> load program
 39903                                  ;
 39904                                  ;	    +---------------------------+
 39905                                  ;	    | WORD segment address of	|
 39906                                  ;	    | environment.		|
 39907                                  ;	    +---------------------------+
 39908                                  ;	    | DWORD pointer to ASCIZ	|
 39909                                  ;	    | command line at 80h	|
 39910                                  ;	    +---------------------------+
 39911                                  ;	    | DWORD pointer to default	|
 39912                                  ;	    | FCB to be passed at 5Ch	|
 39913                                  ;	    +---------------------------+
 39914                                  ;	    | DWORD pointer to default	|
 39915                                  ;	    | FCB to be passed at 6Ch	|
 39916                                  ;	    +---------------------------+
 39917                                  ;	    | DWORD returned value of	|
 39918                                  ;	    | CS:IP			|
 39919                                  ;	    +---------------------------+
 39920                                  ;	    | DWORD returned value of	|
 39921                                  ;	    | SS:IP			|
 39922                                  ;	    +---------------------------+
 39923                                  ;
 39924                                  ;	    AL = 3 -> load overlay
 39925                                  ;
 39926                                  ;	    +---------------------------+
 39927                                  ;	    | WORD segment address where|
 39928                                  ;	    | file will be loaded.	|
 39929                                  ;	    +---------------------------+
 39930                                  ;	    | WORD relocation factor to |
 39931                                  ;	    | be applied to the image.	|
 39932                                  ;	    +---------------------------+
 39933                                  ;
 39934                                  ; Returns:
 39935                                  ;	    AX = error_invalid_function
 39936                                  ;	       = error_bad_format
 39937                                  ;	       = error_bad_environment
 39938                                  ;	       = error_not_enough_memory
 39939                                  ;	       = error_file_not_found
