 42491                                  ;   Assembler usage:
 42492                                  ;           MOV     ES,block
 42493                                  ;           MOV     BX,newsize
 42494                                  ;           MOV     AH,setblock
 42495                                  ;           INT     21h
 42496                                  ;         if setblock fails for growing, BX will have the maximum
 42497                                  ;         size possible
 42498                                  ;   Error return:
 42499                                  ;           AX = error_invalid_block
 42500                                  ;              = error_arena_trashed
 42501                                  ;              = error_not_enough_memory
 42502                                  ;              = error_invalid_function
