 42603                                  ;----------------------------------------------------------------------------
 42604                                  ;**	$AllocOper - Get/Set Allocation Mechanism
 42605                                  ;
 42606                                  ;	Assembler usage:
 42607                                  ;           MOV     AH,AllocOper
 42608                                  ;           MOV     BX,method
 42609                                  ;           MOV     AL,func
 42610                                  ;           INT     21h
 42611                                  ;
 42612                                  ;	ENTRY	
 42613                                  ;		(al) = 0
 42614                                  ;		  Get allocation Strategy in (ax)
 42615                                  ;
 42616                                  ;		(al) = 1, (bx) = method = zw0000xy
 42617                                  ;		  Set allocation strategy.
 42618                                  ;		   w  = 1  => HIGH_ONLY
 42619                                  ;		   z  = 1  => HIGH_FIRST
 42620                                  ;		   xy = 00 => FIRST_FIT
 42621                                  ;		      = 01 => BEST_FIT
 42622                                  ;		      = 10 => LAST_FIT
 42623                                  ;
 42624                                  ;		(al) = 2
 42625                                  ;		  Get UMB link state in (al)
 42626                                  ;
 42627                                  ;		(al) = 3
 42628                                  ;		  Set UMB link state
 42629                                  ;		   (bx) = 0 => Unlink UMBs
 42630                                  ;		   (bx) = 1 => Link UMBs
 42631                                  ;
 42632                                  ;
 42633                                  ;	EXIT	'C' clear if OK
 42634                                  ;
 42635                                  ;		 if (al) = 0
 42636                                  ;		  (ax) = existing method
 42637                                  ;		 if (al) = 1
 42638                                  ;		  Sets allocation strategy
 42639                                  ;		 if (al) = 2
 42640                                  ;		  (al) = 0 => UMBs not linked
 42641                                  ;		  (al) = 1 => UMBs linked in
 42642                                  ;		 if (al) = 3
 42643                                  ;		  Links/Unlinks the UMBs into DOS chain
 42644                                  ;
 42645                                  ;		'C' set if error
 42646                                  ;		  AX = error_invalid_function
 42647                                  ;
 42648                                  ;	Rev. M000 - added support for HIGH_FIRST in (al) = 1. 7/9/90
 42649                                  ; 	Rev. M003 - added functions (al) = 2 and (al) = 3. 7/18/90
 42650                                  ;	Rev. M009 - (al) = 3 will return 'invalid function' in ax if
 42651                                  ;		    umbhead has'nt been initialized by sysinit and 'trashed
 42652                                  ;		    arena' if an arena sig is damaged.
 42653                                  ;----------------------------------------------------------------------------
 42654                                  
