 48257                                  ;Break <ShareEnter - attempt to enter a node into the sharing set>
 48258                                  ;----------------------------------------------------------------------------
 48259                                  ;   ShareEnter - perform a retried entry of a nodde into the sharing set. If
 48260                                  ;   the max number of retries is exceeded, we notify the user via int 24.
 48261                                  ;
 48262                                  ;   Inputs:     ThisSFT points to the SFT
 48263                                  ;               WFP_Start points to the WFP
 48264                                  ;   Outputs:    Carry clear => successful entry
 48265                                  ;               Carry set => failed system call
 48266                                  ;   Registers modified: all
 48267                                  ;----------------------------------------------------------------------------
 48268                                  
 48269                                  ShareEnter:
 48270 00008491 51                      	push	cx
 48271                                  retry:
 48272 00008492 8B0E[1A00]              	mov     cx,[RetryCount]
 48273                                  attempt:
 48274 00008496 C43E[9E05]              	les     di,[THISSFT]		; grab sft
 48275 0000849A 31C0                    	XOR     AX,AX
 48276                                   	;mov	[es:di+51],axfff
 48277 0000849C 26894533                	MOV     [ES:DI+SF_ENTRY.sf_MFT],AX ; indicate free SFT
 48278 000084A0 51                      	push	cx
 48279 000084A1 E8BDFF                  	call    SHARE_CHECK             ; attempt to enter into the sharing set
 48280 000084A4 59                      	pop	cx
 48281 000084A5 730A                    	jnc	short done		; success, let the user see this
 48282 000084A7 E83893                  	call	Idle                    ; wait a while
 48283 000084AA E2EA                    	loop    attempt                 ; go back for another attempt
 48284 000084AC E8B7FF                  	call    SHARE_VIOLATION         ; signal the problem to the user
 48285 000084AF 73E1                    	jnc	short retry		; user said to retry, go do it
 48286                                  done:
 48287 000084B1 59                      	pop	cx
 48288 000084B2 C3                      	retn
