 48504                                  
 48505                                  ;	cli
 48506                                  ;	in	al, 21h
 48507                                  ;	pusha
 48508                                  ;	xor	ax, ax
 48509                                  ;	out	43h, al
 48510                                  ;	mov	dx, ...
 48511                                  
 48512                                  rpFind1a:
 48513                                  	db	0B0h, 0Eh, 0E6h, 37h, 33h, 0C0h, 0E6h, 0F2h
 48514                                  
