 48763                                  struc StackVars
 48764                                   .sv_wVersion:	resw 1		; Rational extender version #
 48765                                   .sv_cbCodeSeg: resw 1		; code seg size to scan
 48766                                   .sv_pPatch:	resw 1		; offset of next avail patch byte
