 48771                                  
 48772                                  ; 22/05/2019 - Retro DOS v4.0
 48773                                  
 48774                                  Rational386Patch:
 48775                                  	; Do a few quick checks to see if this looks like a Rational
 48776                                  	; Extended application. Hopefully this will quickly weed out
 48777                                  	; most non Rational apps.
 48778                                  
 48779                                  	cmp	word [es:0],395		; version number goes here - versions
 48780                                  	jae	short rp3QuickOut	;   3.95+ don't need patching
 48781                                  
 48782                                  	cmp	word [es:0Ch],20h	; always has this value here
 48783                                  	jne	short rp3QuickOut
 48784                                  
 48785                                  	push	ax
 48786                                  
 48787                                  	mov	ax,18h 			; extender has 18h at
 48788                                  	cmp	[es:24],ax		;   offsets 24, 28, & 36
 48789                                  	jne	short rp3QO_ax
 48790                                  	cmp	[es:28],ax
 48791                                  	jne	short rp3QO_ax
 48792                                  	cmp	[es:36],ax
 48793                                  	je	short rp3Maybe
 48794                                  rp3QO_ax:
 48795                                  	pop	ax
 48796                                  rp3QuickOut:
 48797                                  	retn
 48798                                  
 48799                                  ; It might be the rational extender, do more extensive checking
 48800                                  
 48801                                  rp3Maybe:
 48802                                  	cld
 48803                                  	push	bx			; note ax pushed above
 48804                                  	push	cx
 48805                                  	push	dx
 48806                                  	push	si
 48807                                  	push	di
 48808                                  	push	es
 48809                                  	push	ds			; we use all of them
 48810                                  	push	bp
 48811                                  	sub	sp,StackVars.size  ; 6	; make space for stack variables
 48812                                  	mov	bp,sp
 48813                                  
 48814                                  	push	cs
 48815                                  	pop	ds
 48816                                  
 48817                                  	mov	ax,[es:0]		; save version #
 48818                                  	;mov	[bp+StackVars.sv_wVersion],ax
 48819                                  	mov	[bp],ax	
 48820                                  					; check that binary version # matches
 48821                                  	call	VerifyVersion		;   ascii string
 48822                                  	jne	short rp3Exit_j
 48823                                  
 48824                                  ; Looks like this is it, find where to put the patch code.  The
 48825                                  ; patch will be located on top of Rational code specific to 80286
 48826                                  ; processors, so these patchs MUST NOT be applied if running on
 48827                                  ; an 80286 system.
 48828                                  
 48829                                  	; Rational says the code to patch will never be beyond offset 46xxh
 48830                                  
 48831                                  	mov	cx,4500h		; force search len to 4700h (searches
 48832                                  	;mov	[bp+2],cx
 48833                                  	mov	[bp+StackVars.sv_cbCodeSeg],cx	; start at offset 200h)
 48834                                  
 48835                                  	mov	es,[es:20h]		; es=code segment
 48836                                  
 48837                                  	mov	si,rpFind1		; string to find
 48838                                  	mov	dx,rpFind1Len ; 10	; length to match
 48839                                  	call	ScanCodeSeq		; look for code seq
 48840                                  	jz	short rpGotPatch
 48841                                  
 48842                                  ; According to Rational, some very old versions of the extender may not
 48843                                  ; have the find1 code sequence. If the find1 code wasn't found above,
 48844                                  ; try an alternative patch area which is on top of NEC 98xx switching code.
 48845                                  
 48846                                  	mov	si,rpFind1a
 48847                                  	mov	dx,rpFind1aLen  ;8
 48848                                  	call	ScanCodeSeq
 48849                                  	jz	short rpGotPatch
 48850                                  
 48851                                  rp3Exit_j:
 48852                                  	jmp	rp3Exit
 48853                                  
 48854                                  ; Found the location to write patch code! DI = offset in code seg.
 48855                                  
 48856                                  rpGotPatch:
 48857                                  	;mov	[bp+4],di
 48858                                  	mov	[bp-StackVars.sv_pPatch],di	; save patch pointer
 48859                                  
 48860                                  ;----------------------------------------------------------------------------
 48861                                  ; Bug # 1 -- loss of high EAX on 386+ if not VCPI or DPMI
 48862                                  
 48863                                  	;cmp	word [bp+0],381
 48864                                  	;cmp	word [bp+StackVars.sv_wVersion],381 ; only need bug 1 if version
 48865                                  	cmp	word [bp],381
 48866                                  	jae	short rpBug2			;   < 3.81
 48867                                  
 48868                                  	mov	bx,rpBug1Strs			; locate find2 & find3 code
 48869                                  	call	FindBadCode
 48870                                  	jc	short rpBug2
 48871                                  
 48872                                  ; si = rpFind2 offset, di = rpFind3 offset
 48873                                  
 48874                                  	push	di
 48875                                  	mov	di,si				; rpFind2 offset
 48876                                  	mov	dx,rpFind2Len ; 3
 48877                                  
 48878                                  	cmp	byte [es:di-1],51h	 	; find2 preceeded by push cx?
 48879                                  	jne	short rp_no_cx
 48880                                  
 48881                                  	dec	di				;   yes, gobble up push cx too
 48882                                  	inc	dx
 48883                                  rp_no_cx:
 48884                                  	mov	si,rpRepl2			; patch out find2 sequence
 48885                                  	mov	cx,rpRepl2Len  ; 6
 48886                                  	call	GenPatch
 48887                                  
 48888                                  	pop	di				; rpFind3 offset
 48889                                  	cmp	byte [es:di-1],59h 		; find3 preceeded by pop cx?
 48890                                  	jne	short rp_no_cx2
 48891                                  
 48892                                  	mov	byte [es:di-1],90h		;   yes, no-op it
 48893                                  rp_no_cx2:
 48894                                  	;mov	ax,[bp+4]
 48895                                  	mov	ax,[bp+StackVars.sv_pPatch]	; change offset of far jmp
 48896                                  	;mov	[es:di+4],ax
 48897                                  	mov	[es:di+rpFind3Len],ax		;   to go to patch code
 48898                                  
 48899                                  	push	di				; save find3 offset
 48900                                  	mov	si,rpRepl3			; copy repl3 to patch area
 48901                                  	mov	cx,rpRepl3Len ; 5
 48902                                  	call	CopyPatch
 48903                                  
 48904                                  	pop	bx				; find3 offset
 48905                                  	add	bx,rpFind3Len+4	 ; 8		; skip over find3 and far jmp
 48906                                  	call	GenJump 			; jmp back from patch area
 48907                                  	;mov	[bp+4],di
 48908                                  	mov	[bp+StackVars.sv_pPatch], di	;   to main-line, update patch
 48909                                  						;   area pointer
 48910                                  
 48911                                  ;----------------------------------------------------------------------------
 48912                                  ; Bug # 2 -- loss of high regs on 386+ under VCPI only
 48913                                  
 48914                                  rpBug2:
 48915                                  	mov	bx,rpBug2Strs			; locate find4 & find5 code
 48916                                  	call	FindBadCode
 48917                                  	jc	short rpBug3
 48918                                  
 48919                                  ; si = rpFind4 offset, di = rpFind5 offset
 48920                                  
 48921                                  	;push	word [bp+4]
 48922                                  	push	word [bp+StackVars.sv_pPatch]	; save current patch pointer
 48923                                  						;   (where repl4 goes)
 48924                                  	push	di				; save find5 offset
 48925                                  
 48926                                  	mov	di,si
 48927                                  	mov	dx,rpFind4Len ; 4
 48928                                  	mov	si,rpRepl4
 48929                                  	mov	cx,rpRepl4Len ; 15
 48930                                  	call	GenPatch			; patch out find4 code
 48931                                  
 48932                                  	pop	di				; find5 offset
 48933                                  	add	di,5				; keep 5 bytes of find5 code
 48934                                  	;mov	bx,[bp+4]
 48935                                  	mov	bx,[bp+StackVars.sv_pPatch]	; jump to patch area
 48936                                  	push	bx				; save repl5 location
 48937                                  	call	GenJump
 48938                                  
 48939                                  	mov	si,rpRepl5			; copy repl5 code to patch
 48940                                  	mov	cx,rpRepl5Len  ; 15		;   area -- it has a jmp bx
 48941                                  	call	CopyPatch			;   so no need to jmp back to
 48942                                  						;   main-line code
 48943                                  
 48944                                  ; patches have been made, now update the patch code to store/load dwords just
 48945                                  ; after the code in the patch area
 48946                                  
 48947                                  	pop	di				; repl5 location
 48948                                  	pop	si				; repl4 location
 48949                                  
 48950                                  	;mov	ax,[bp+4]
 48951                                  	mov	ax,[bp+StackVars.sv_pPatch]	; (where dwords go)
 48952                                  
 48953                                  	;mov	[es:si+7],ax
 48954                                  	mov	[es:si+rpRepl4o1Len],ax		; offset for EAX
 48955                                  	;mov	[es:di+5],ax
 48956                                  	mov	[es:di+rpRepl5o1Len],ax
 48957                                  	add	ax,4
 48958                                  	;mov	[es:si+0Dh],ax
 48959                                  	mov	[es:si+rpRepl4o2Len],ax		; offset for ESI
 48960                                  	;mov	[es:di+0Bh],ax
 48961                                  	mov	[es:di+rpRepl5o2Len],ax
 48962                                  
 48963                                  	;add	word [bp+4],8
 48964                                  	add	word [bp+StackVars.sv_pPatch],8	; reserve space for 2 dwords in
 48965                                  						; patch area
 48966                                  
 48967                                  ;----------------------------------------------------------------------------
 48968                                  ; Bug # 3 -- loss of high regs on 386+ under VCPI only
 48969                                  
 48970                                  rpBug3:
 48971                                  	mov	bx,rpBug3Strs			; locate find6 & find7a code
 48972                                  	call	FindBadCode
 48973                                  	jc	short rpBug4
 48974                                  
 48975                                  	;add	di,9
 48976                                  	add	di,rpFind7aLen + 2		; skip over offset in find7a
 48977                                  	push	si				;   code and locate find7b
 48978                                  	mov	si,rpFind7b			;   sequence
 48979                                  	mov	dx,rpFind7bLen ; 3
 48980                                  	call	ScanCodeSeq_di
