 48988                                  	mov	si,rpRepl6
 48989                                  	mov	cx,rpRepl6Len ; 9
 48990                                  	call	GenPatch			; patch out find6 code
 48991                                  
 48992                                  	pop	di
 48993                                  	mov	dx,rpFind7bLen ; 3
 48994                                  	mov	si,rpRepl7
 48995                                  	mov	cx,rpRepl7Len ; 9
 48996                                  	call	GenPatch			; patch out find7b code
 48997                                  
