 49000                                  
 49001                                  rpBug4:
 49002                                  	;cmp	word [bp+0],360
 49003                                  	;cmp	word [bp+StackVars.sv_wVersion],360 ; only applies if 
 49004                                  	cmp	word [bp],360
 49005                                  	jbe	short rp3Exit 			; version > 3.60 and < 3.95
 49006                                  
 49007                                  	mov	bx,rpBug4Strs			; locate find8 & find9 code
 49008                                  	call	FindBadCode
 49009                                  	jc	short rp3Exit
 49010                                  
