 49022                                  	call	GenJump 			;   patch area
 49023                                  
 49024                                  	mov	si,rpRepl9			; copy replacement code to
 49025                                  	mov	cx,rpRepl9Len ; 5		;   patch area--it does a RET
 49026                                  	call	CopyPatch			;   so no jmp back to main-line
 49027                                  
 49028                                  rp3Exit:
 49029                                  	add	sp,StackVars.size
 49030                                  	pop	bp
 49031                                  	pop	ds
 49032                                  	pop	es
 49033                                  	pop	di
 49034                                  	pop	si
 49035                                  	pop	dx
 49036                                  	pop	cx
 49037                                  	pop	bx
