 49041                                  ;----------------------------------------------------------------------------
 49042                                  ;
 49043                                  ; FindBadCode
 49044                                  ;
 49045                                  ; Searches Rational code segment looking for a pair of find strings (all
 49046                                  ; patches have at least two find strings).
 49047                                  ;
 49048                                  ; Entry:
 49049                                  ;	ES    = code segment to search
 49050                                  ;	DS:BX = search pair structure for this search
 49051                                  ;	[bp].sv_cbCodeSeg = length of code seg to search
 49052                                  ;
 49053                                  ; Exit:
 49054                                  ;	CY flag clear if both strings found, and
 49055                                  ;	SI    = offset in ES of 1st string
 49056                                  ;	DI    = offset in ES of 2nd string
 49057                                  ;	CY set if either string not found, or strings too far apart
 49058                                  ;
 49059                                  ; Used:
 49060                                  ;	CX
 49061                                  ;
 49062                                  ;----------------------------------------------------------------------------
 49063                                  
 49064                                  ;struc SearchPair
 49065                                  ; .sp_off1: resw 1	; offset of 1st search string
 49066                                  ; .sp_len1: resw 1	; length of 1st search string
 49067                                  ; .sp_off2: resw 1	; 2nd string
 49068                                  ; .sp_len2: resw 1	; 2nd string
 49069                                  ; .sp_diff: resw 1	; max difference between offsets
 49070                                  ; .size:
 49071                                  ;endstruc
 49072                                  
 49073                                  FindBadCode:
 49074                                  	;mov	cx,[bp+2]
 49075                                  	mov	cx,[bp+StackVars.sv_cbCodeSeg]	; search length
 49076                                  
 49077                                  	mov	si,[bx]	; mov si,[bx+0]
 49078                                  	;mov	si,[bx+Searchpair.sp_off1] ; ds:si -> search string
 49079                                  	
 49080                                  	;mov	dx,[bx+2]
 49081                                  	mov	dx,[bx+SearchPair.sp_len1] ; dx = search len
 49082                                  	call	ScanCodeSeq
 49083                                  	jnz	short fbc_error		; done if 1st not found
 49084                                  
 49085                                  	push	di			; save 1st string offset
 49086                                  
 49087                                  	;mov	si,[bx+4]
 49088                                  	mov	si,[bx+SearchPair.sp_off2]
 49089                                  	;mov	dx,[bx+6]
 49090                                  	mov	dx,[bx+SearchPair.sp_len2]
 49091                                  	call	ScanCodeSeq_di		; don't change flags after this!
 49092                                  
 49093                                  	pop	si			; restore 1st string offset
 49094                                  	jnz	short fbc_error
 49095                                  
 49096                                  	mov	ax,di			; sanity check that
 49097                                  	sub	ax,si			;   si < di && di - si <= allowed diff
 49098                                  	jc	short fbc_error
 49099                                  	;cmp	ax,[bx+8]
 49100                                  	cmp	ax,[bx+SearchPair.sp_diff]
 49101                                  	ja	short fbc_error
 49102                                  
 49103                                  	clc
 49104                                  	retn
 49105                                  
 49106                                  fbc_error:
 49107                                  	stc
 49108                                  	retn
 49109                                  
 49110                                  ;----------------------------------------------------------------------------
 49111                                  ;
 49112                                  ; GenPatch
 49113                                  ;
 49114                                  ; Generate a patch sequence. 1) insert a jump at the buggy code location
 49115                                  ; (jumps to the patch code area), 2) copy the selected patch code to the
 49116                                  ; patch area, 3) insert a jump from the patch area back to the main-line
 49117                                  ; code.
 49118                                  ;
 49119                                  ; Entry:
 49120                                  ;	ES:DI = start of buggy code to be patched
 49121                                  ;	DX    = length of buggy code to be patched
 49122                                  ;	DS:SI = replacement patch code
 49123                                  ;	CX    = length of replacement patch code
 49124                                  ;	[bp].sv_pPatch = offset in ES of where to copy patch code
 49125                                  ;
 49126                                  ; Exit:
 49127                                  ;	DI, [bp].sv_pPatch = byte after generated patch code
 49128                                  ;
 49129                                  ; Used:
 49130                                  ;	AX, BX, SI, Flags
 49131                                  ;
 49132                                  ;----------------------------------------------------------------------------
 49133                                  
 49134                                  GenPatch:
 49135                                  	push	di			;save offset of buggy code
 49136                                  
 49137                                  	;mov	bx,[bp+4]
 49138                                  	mov	bx,[bp+StackVars.sv_pPatch]
 49139                                  					;jump from buggy code to patch area
 49140                                  	call	GenJump
 49141                                  
 49142                                  	call	CopyPatch		;copy replacement code to patch area
 49143                                  
 49144                                  	pop	bx			;offset of buggy code + buggy code
 49145                                  	add	bx,dx			;  length = return from patch offset
 49146                                  
 49147                                  	call	GenJump 		;jump from patch area back to main-
 49148                                  	;mov	[bp+4],di
 49149                                  	mov	[bp+StackVars.sv_pPatch],di
 49150                                  					;  line code, update patch pointer
 49151                                  	retn
