 49153                                  ;----------------------------------------------------------------------------
 49154                                  ;
 49155                                  ; CopyPatch
 49156                                  ;
 49157                                  ; Copies patch code to patch location.
 49158                                  ;
 49159                                  ; Entry:
 49160                                  ;	DS:SI = patch code to be copied
 49161                                  ;	ES    = segment of code to patch
 49162                                  ;	CX    = length of code to copy
 49163                                  ;	[bp].sv_pPatch = offset in ES of where to copy patch code
 49164                                  ;
 49165                                  ; Exit:
 49166                                  ;	DI, [bp].sv_pPatch = byte after copied patch code
 49167                                  ;
 49168                                  ; Used:
 49169                                  ;	SI, Flags
 49170                                  ;
 49171                                  ;----------------------------------------------------------------------------
 49172                                  
 49173                                  CopyPatch:
 49174                                  	push	cx
 49175                                  	;mov	di,[bp+4]
 49176                                  	mov	di,[bp+StackVars.sv_pPatch] ;patch pointer is the dest offset
 49177                                  	cld
 49178                                  	rep movsb
 49179                                  	pop	cx
 49180                                  	;mov	[bp+4],di
 49181                                  	mov	[bp+StackVars.sv_pPatch],di ;update net pointer location
 49182                                  	retn
 49183                                  
 49184                                  ;----------------------------------------------------------------------------
 49185                                  ;
 49186                                  ; GenJump
 49187                                  ;
 49188                                  ; Generates a rel16 JMP instruction at location 'from' to location 'to'.
 49189                                  ;
 49190                                  ; Entry:
 49191                                  ;	ES:DI = from location (where to put jmp instruction)
 49192                                  ;	BX    = to location (where to jump to)
 49193                                  ;
 49194                                  ; Exit:
 49195                                  ;	DI = byte after generated jump
 49196                                  ;
 49197                                  ; Used:
 49198                                  ;	AX
 49199                                  ;
 49200                                  ;----------------------------------------------------------------------------
 49201                                  
 49202                                  GenJump:
 49203                                  	mov	al,0E9h		; jmp rel16 opcode
 49204                                  	stosb
 49205                                  
 49206                                  	mov	ax,bx		; calc offset to 'to' location
 49207                                  	sub	ax,di
 49208                                  	sub	ax,2
 49209                                  
 49210                                  	stosw			; output offset
 49211                                  
 49212                                  	retn
 49213                                  
 49214                                  ;----------------------------------------------------------------------------
 49215                                  ;
 49216                                  ; ScanCodeSeq
 49217                                  ;
 49218                                  ; Looks for a pattern pointed to by DS:SI & len DX in ES:200 to ES:200+CX-1
 49219                                  ;
 49220                                  ; returns in ES:DI the start of the pattern if Zero flag is set
 49221                                  ;
 49222                                  ;----------------------------------------------------------------------------
 49223                                  
 49224                                  ScanCodeSeq:
 49225                                  	mov	di,200h
 49226                                  ScanCodeSeq_di:
 49227                                  	push	cx
 49228                                  	sub	cx,dx
 49229                                  	inc	cx
 49230                                  scsagain:
 49231                                  	push	si
 49232                                  	push	di
 49233                                  	push	cx
 49234                                  	mov	cx,dx
 49235                                  	rep	cmpsb
 49236                                  	pop	cx
 49237                                  	pop	di
 49238                                  	pop	si
 49239                                  	je	short scsfound
 49240                                  	inc	di
 49241                                  	loop	scsagain
 49242                                  scsfound:
 49243                                  	pop	cx
 49244                                  vvexit:		; 18/12/2022
 49245                                  	retn
 49246                                  	
 49247                                  ;----------------------------------------------------------------------------
 49248                                  ;
 49249                                  ; VerifyVersion
 49250                                  ;
 49251                                  ; Checks whether the binary version from ES:0 matches the ASCII version
 49252                                  ; from ES:2A.
 49253                                  ;
 49254                                  ;       Entry: AX = binary version number 
 49255                                  ;       Exit : Z flag set if version numbers match
 49256                                  ;
 49257                                  ;----------------------------------------------------------------------------
 49258                                  
 49259                                  VerifyVersion:
 49260                                  	mov	si,[es:2Ah]		; offset of version number
 49261                                  					;  in ascii
 49262                                  	mov	bl,10
 49263                                  	add	si,3			; point to last digit
 49264                                  
 49265                                  	call	VVDigit
 49266                                  	jne	short vvexit
 49267                                  	call	VVDigit
 49268                                  	jne	short vvexit
 49269                                  	cmp	byte [es:si],'.'
 49270                                  	jne	short vvexit
 49271                                  	dec	si
 49272                                  	;call	VVDigit
 49273                                  	; 18/12/2022
 49274                                  	;jmp	short VVDigit
 49275                                  ;vvexit:
 49276                                  	;retn
 49277                                  VVDigit:
 49278                                  	div	bl
 49279                                  	add	ah,'0'
 49280                                  	dec	si
 49281                                  	cmp	[es:si+1],ah
 49282                                  	mov	ah,0			; do not xor or sub we need Z
 49283                                  	retn
 49284                                  
 49285                                  %endif
