 49869                                  ;
 49870                                  ; procedure : RationalPatch
 49871                                  ;
 49872                                  ; A routine (in Ration DOS extender) which is invoked at hardware interrupts
 49873                                  ; clobbers CX register on 286 machines. (123 release 3 uses Rational DOS
 49874                                  ; extender). This routine identifies Buggy Rational EXEs and fixes the bug.
 49875                                  ;
 49876                                  ; THE BUG is in the following code sequence:
 49877                                  ;
 49878                                  ;8b 0e 10 00	mov	cx, ds:[10h]		; delay count
 49879                                  ;90		even				; word align
 49880                                  ;e2 fe		loop	$			; wait		CLOBBERS CX
 49881                                  ;e8 xx xx	call	set_A20			; enable A20
 49882                                  ;
 49883                                  ; This patch routine replaces the mov & the loop with a far call into a
 49884                                  ; routine in DOS data segment which is in low memory (because A20 line
 49885                                  ; is off). The routine (RatBugCode) in DOS data saves & restores CX around
 49886                                  ; a mov & loop.
 49887                                  ;
 49888                                  ; Identification of Buggy Rational EXE
 49889                                  ; ====================================
 49890                                  ;
 49891                                  ; (ALL OFFSETS ARE IN THE PROGRAM SECTION - EXCLUDING THE EXE HEADER)
 49892                                  ;
 49893                                  ; OFFSET				Contains
 49894                                  ; ------				--------
 49895                                  ; 0000h			100 times Version number in binary
 49896                                  ;			bug exists in version 3.48 thru 3.83 (both inclusive)
 49897                                  ;
 49898                                  ; 000ah			the WORDS : 0000h, 0020h, 0000h, 0040h, 0001h
 49899                                  ;
 49900                                  ; 002ah			offset where version number is stored in ASCII
 49901                                  ;				e.g. '3.48A'
 49902                                  ;
 49903                                  ; 0030h			offset of copyright string. Copyright strings either
 49904                                  ;			start with "DOS/16M Copyright...." or
 49905                                  ;			"Copyright.....". The string contains
 49906                                  ;			"Rational Systems, Inc."
 49907                                  ;
 49908                                  ; 0020h			word : Paragraph offset of the buggy code segment
 49909                                  ;				from the program image
 49910                                  ; 0016h			word : size of buggy code segment
 49911                                  ;
 49912                                  ;	Buggy code is definite to start after offset 200h in its segment
 49913                                  ;
 49914                                  ;----------------------------------------------------------------------------
 49915                                  
 49916                                  ; 23/05/2019 - Retro DOS v4.0
 49917                                  ; DOSCODE:B976h (MSDOS 6.21, MSDOS.SYS)
 49918                                  
 49919                                  ; 04/12/2022 - Retro DOS v4.0 (Modified MSDOS 5.0 MSDOS.SYS)
 49920                                  ; DOSCODE:B654h (MSDOS 5.0, MSDOS.SYS)
 49921                                  
 49922                                  RScanPattern1:
 49923                                  	db	0, 0, 20h, 0, 0, 0, 40h, 0, 1, 0
 49924                                  
 49925                                  RLen1 equ $ - RScanPattern1
 49926                                  
 49927                                  RScanPattern2:
 49928                                  	db	8Bh, 0Eh, 10h, 00h, 90h, 0E2h, 0FEh, 0E8h
 49929                                  
 49930                                  RLen2 equ $ - RScanPattern2
 49931                                  
 49932                                  RScanPattern3:
 49933                                  	db	8Bh, 0Eh, 10h, 00h, 0E2h, 0FEh, 0E8h
 49934                                  
 49935                                  RLen3 equ $ - RScanPattern2
 49936                                  
 49937                                  ; DOSCODE:B98Fh (MSDOS 6.21, MSDOS.SYS)
 49938                                  ; DOSCODE:B66Dh (MSDOS 5.0, MSDOS.SYS)
 49939                                  
 49940                                  ;----------------------------------------------------------------------------
 49941                                  ;
 49942                                  ; INPUT : ES = segment where program got loaded
 49943                                  ;
 49944                                  ;----------------------------------------------------------------------------
 49945                                  
 49946                                  RationalPatch:
 49947                                  	cld
 49948                                  	push	ax
 49949                                  	push	bx
 49950                                  	push	cx
 49951                                  	push	dx
 49952                                  	push	si
 49953                                  	push	di
 49954                                  	push	es
 49955                                  	push	ds			; we use all of them
 49956                                  	mov	di,0Ah			; look for pat1 at offset 0Ah
 49957                                  	push	cs
 49958                                  	pop	ds
 49959                                  	
 49960                                  	mov	si,RScanPattern1
 49961                                  	;mov	cx,10
 49962                                  	mov	cx,RLen1
 49963                                  	rep	cmpsb			; do we have the pattern ?
 49964                                  	jne	short rpexit
 49965                                  	mov	ax,[es:0]
 49966                                  	cmp	ax,348			; is it a buggy version ?
 49967                                  	jb	short rpexit
 49968                                  	cmp	ax,383			; is it a buggy version
 49969                                  	ja	short rpexit
 49970                                  
 49971                                  	call	VerifyVersion
 49972                                  	jne	short rpexit
 49973                                  
 49974                                  	mov	cx,[es:16h]		; Length of buggy code seg
 49975                                  	sub	cx,200h			; Length we search (we start
 49976                                  					;  at offset 200h)
 49977                                  	mov	es,[es:20h]		; es=buggy code segment
 49978                                  	mov	si,RScanPattern2
 49979                                  	;mov	dx,8	
 49980                                  	mov	dx,RLen2
 49981                                  	call	ScanCodeSeq		; look for code seq with nop
 49982                                  	jz	short rpfound
 49983                                  
 49984                                  	mov	si,RScanPattern3
 49985                                  	;mov	dx,15
 49986                                  	mov	dx,RLen3
 49987                                  	call	ScanCodeSeq		; look for code seq w/o nop
 49988                                  	jnz	short rpexit
 49989                                  
 49990                                  rpfound:
 49991                                  	
 49992                                  ;	we set up a far call into DOS data
 49993                                  ;	dx has the length of the code seq we were searching for
 49994                                  
 49995                                  	mov	al,9Ah			; far call opcode
 49996                                  	stosb
 49997                                  	mov	ax,RatBugCode
 49998                                  	stosw
 49999                                  	mov	ax,ss
 50000                                  	stosw
 50001                                  	mov	cx,dx
 50002                                  	sub	cx,6			; filler (with NOPs)
 50003                                  	mov	al,90h
 50004                                  	rep	stosb
 50005                                  rpexit:
 50006                                  	pop	ds
 50007                                  	pop	es
 50008                                  	pop	di
 50009                                  	pop	si
 50010                                  	pop	dx
 50011                                  	pop	cx
 50012                                  	pop	bx
 50013                                  	pop	ax
 50014                                  	retn
 50015                                  
 50016                                  ; M020 END
 50017                                  
 50018                                  ; 04/12/2022 - Retro DOS v4.0 (Modified MSDOS 5.0 MSDOS.SYS)
 50019                                  ; (MSDOS 5.0 MSDOS.SYS compatibility)
 50020                                  
 50021                                  ; DOSCODE:B6D8h (MSDOS 5.0, MSDOS.SYS)
 50022                                  
 50023                                  ;----------------------------------------------------------------------------
 50024                                  ;
 50025                                  ; ScanCodeSeq
 50026                                  ;
 50027                                  ; Looks for a pattern pointed to by DS:SI & len DX in ES:200 to ES:200+CX-1
 50028                                  ;
 50029                                  ; returns in ES:DI the start of the pattern if Zero flag is set
 50030                                  ;
 50031                                  ;----------------------------------------------------------------------------
 50032                                  
 50033                                  ScanCodeSeq:
 50034                                  	; 17/12/2022
 50035                                  	mov	di,200h
 50036                                  ;ScanCodeSeq_di:
 50037                                  	push	cx
 50038                                  	sub	cx,dx
 50039                                  	inc	cx
 50040                                  	; 17/12/2022
 50041                                  	; 04/12/2022 (MSDOS 5.0 MSDOS.SYS compatibility)
 50042                                  	;mov	di,200h
 50043                                  scsagain:
 50044                                  	push	si
 50045                                  	push	di
 50046                                  	push	cx
 50047                                  	mov	cx,dx
 50048                                  	rep	cmpsb
 50049                                  	pop	cx
 50050                                  	pop	di
 50051                                  	pop	si
 50052                                  	je	short scsfound
 50053                                  	inc	di
 50054                                  	loop	scsagain
 50055                                  scsfound:
 50056                                  	pop	cx
 50057                                  vvexit:		; 18/12/2022
 50058                                  	retn
 50059                                  
 50060                                  
 50061                                  ; 04/12/2022 - Retro DOS v4.0 (Modified MSDOS 5.0 MSDOS.SYS)
 50062                                  ; (MSDOS 5.0 MSDOS.SYS compatibility)
 50063                                  
 50064                                  ; DOSCODE:B6F0h (MSDOS 5.0, MSDOS.SYS)
 50065                                  
 50066                                  ;----------------------------------------------------------------------------
 50067                                  ;
 50068                                  ; VerifyVersion
 50069                                  ;
 50070                                  ; Checks whether the binary version from ES:0 matches the ASCII version
 50071                                  ; from ES:2A.
 50072                                  ;
 50073                                  ;       Entry: AX = binary version number 
 50074                                  ;       Exit : Z flag set if version numbers match
 50075                                  ;
 50076                                  ;----------------------------------------------------------------------------
 50077                                  
 50078                                  VerifyVersion:
 50079                                  	mov	si,[es:2Ah]		; offset of version number
 50080                                  					;  in ascii
 50081                                  	mov	bl,10
 50082                                  	add	si,3			; point to last digit
 50083                                  
 50084                                  	call	VVDigit
 50085                                  	jne	short vvexit
 50086                                  	call	VVDigit
 50087                                  	jne	short vvexit
 50088                                  	cmp	byte [es:si],'.' ; 2Eh
 50089                                  	jne	short vvexit
 50090                                  	dec	si
 50091                                  	;call	VVDigit
 50092                                  	; 18/12/2022
 50093                                  	;jmp	short VVDigit
 50094                                  ;vvexit:
 50095                                  	;retn
 50096                                  VVDigit:
 50097                                  	div	bl
 50098                                  	add	ah,'0' ; 30h
 50099                                  	dec	si
 50100                                  	cmp	[es:si+1],ah
 50101                                  	mov	ah,0			; do not xor or sub we need Z
 50102                                  	retn
 50103                                  
 50104                                  ;--------------------------------------------------------------------------- 
 50105                                  %endif	; 28/12/2022
