 51292                                  ;
 51293                                  ; Procedure Name : WhatCPUType
 51294                                  ;
 51295                                  ; Inputs	 : none
 51296                                  ;
 51297                                  ; Outputs	 : AL = 0 if CPU <  286
 51298                                  ;		      = 1 if CPU == 286
 51299                                  ;		      = 2 if CPU >= 386
 51300                                  ;
 51301                                  ; Regs. Mod.	 : AX
 51302                                  ;
 51303                                  ;----------------------------------------------------------------------------
 51304                                  
 51305                                  WhatCPUType:
 51306                                  	; 25/04/2019 - Retro DOS v4.0
 51307                                  	;get_cpu_type	; done with a MACRO which can't be generated > once
 51308                                  
 51309                                  	;CPUTYPE.INC (MSDOS 6.0, 1991)
 51310                                  
 51311                                  ; Note: this must be a macro, and not a subroutine in the BIOS since
 51312                                  ; 	it is called from both CODE and SYSINITSEG.
 51313                                  ;
 51314                                  ;------GET_CPU_TYPE-----------------------------------May, 88 by M.Williamson
 51315                                  ;  Returns: AX = 0 if 8086 or 8088
 51316                                  ;              = 1 if 80286
 51317                                  ;              = 2 if 80386
 51318                                  
 51319                                  	; 04/11/2022
 51320                                  	; MSDOS 5.0 MSDOS.SYS - DOSCODE:0BB03h
 51321                                  
 51322                                  Get_CPU_Type:	;macro
 51323                                  	pushf
 51324                                  	push	bx			; preserve bx
 51325                                  	xor	bx,bx			; init bx to zero
 51326                                  
 51327                                  	xor	ax,ax			; 0000 into AX
 51328                                  	push	ax			; put it on the stack...
 51329                                  	popf				; ...then shove it into the flags
 51330                                  	pushf				; get it back out of the flags...
 51331                                  	pop	ax			; ...and into ax
 51332                                  	and	ax,0F000h		; mask off high four bits
 51333                                  	cmp	ax,0F000h		; was it all 1's?
 51334                                  	je	short cpu_8086		; aye; it's an 8086 or 8088
 51335                                  
 51336                                  	mov	ax,0F000h		; now try to set the high four bits..
 51337                                  	push	ax
 51338                                  	popf
 51339                                  	pushf
 51340                                  	pop	ax			; ...and see what happens
 51341                                  	and	ax,0F000h		; any high bits set ?
 51342                                  	jz	short cpu_286		; nay; it's an 80286
 51343                                  
 51344                                  cpu_386:				; bx starts as zero
 51345                                  	inc	bx			; inc twice if 386
 51346                                  cpu_286:				; just inc once if 286
 51347                                  	inc	bx
 51348                                  cpu_8086:				; don't inc at all if 086
 51349                                  	mov	ax,bx			; put CPU type value in ax
 51350                                  	pop	bx			; restore original bx
 51351                                  	popf
 51352                                  	
 51353                                  	;endm
 51354                                  
 51355                                  	; 04/11/2022 (MSDOS 5.0 MSDOS.SYS)
 51356                                  	retn	; 19/09/2023
 51357                                  
