 53720                                  ;------------------------------------------------------------------------
 53721                                  ; Patch for Sidekick
 53722                                  ;
 53723                                  ; A documented method for finding the offset of the Errormode flag in the
 53724                                  ; dos swappable data area if for the app to scan in the dos segment (data)
 53725                                  ; for the following sequence of instructions.
 53726                                  ;
 53727                                  ; Ref: Part C, Article 11, pg 356 of MSDOS Encyclopedia
 53728                                  ;
 53729                                  ; The Offset of Errormode flag is 0320h
 53730                                  ;
 53731                                  ;------------------------------------------------------------------------
 53732                                  
 53733                                  	db	036h, 0F6h, 06h, 020h, 03h, 0FFh ; test ss:[errormode], -1
 53734                                  	db	075h, 0Ch			 ; jnz  NearLabel
 53735                                  	db	036h, 0FFh, 036h, 058h, 03h	 ; push ss:[NearWord]
 53736                                  	db	0CDh, 028h			 ; int  28h
 53737                                  
 53738                                  ;--------------------------------------------------------------------------
 53739                                  ; Patch for PortOfEntry - M036
 53740                                  ;
 53741                                  ; PortOfEntry by Sector Technology uses an un documented way of determining
 53742                                  ; the offset of Errormode flag. The following patch is to support them in
 53743                                  ; DOS 5.0. The corresponding code is actually in msdisp.asm
 53744                                  ;
 53745                                  ;---------------------------------------------------------------------------
 53746                                  
 53747                                  	db 	080h, 03Eh, 020h, 03h, 00h 	 ; cmp 	[errormode], 0
 53748                                  	db	075h, 037h			 ; jnz	NearLabel
 53749                                  	db 	0BCh, 0A0h, 0Ah		  	 ; mov	sp, dosdata:iostack
 53750                                  
 53751                                  %endif ; 29/12/2022
