 53986                                  ;---------------------------------------------------------------------------
 53987                                  ;
 53988                                  ; LowIntXX:
 53989                                  ;
 53990                                  ; Interrupts from DOS that pass control to a user program must be done from
 53991                                  ; low memory, as the user program may change the state of the A20 line or
 53992                                  ; they may require that the A20 line be OFF. The following piece of code is
 53993                                  ; far call'd from the following places in DOS:
 53994                                  ;
 53995                                  ;	1. msctrlc.asm where dos issues an int 23h (ctrlc)
 53996                                  ;	2. msctrlc.asm where dos issues an int 24h (critical error)
 53997                                  ;	3. msctrlc.asm where dos issues an int 28h (idle int)
 53998                                  ;
 53999                                  ; The int 23 and int 24 handlers may decide to do a far return instead of an
 54000                                  ; IRET ane leave the flags on the stack. Therefore we save the return address
 54001                                  ; before doing the ints and then do a far junp back into DOS.
 54002                                  ;
 54003                                  ;---------------------------------------------------------------------------
 54004                                  
 54005 00001123 00000000                DosRetAddr23:	dd	0
 54006 00001127 00000000                DosRetAddr24:	dd	0
 54007 0000112B 00000000                DosRetAddr28:	dd	0
 54008                                  
 54009                                  	; Execute int 23h from low memory
 54010                                  LowInt23:
 54011                                  					; save the return address that is on
 54012                                  					; the stack
 54013 0000112F 2E8F06[2311]            	pop	word [cs:DosRetAddr23]
 54014 00001134 2E8F06[2511]            	pop	word [cs:DosRetAddr23+2]
 54015                                  
 54016 00001139 CD23                    	int	23h			; ctrl C
 54017                                  					; turn on A20 it has been turned OFF
 54018                                  					; by int 28/23/24 handler.
 54019                                  
 54020 0000113B E87100                  	call	EnsureA20ON		; M011: we must turn on A20 if OFF 
 54021                                  
 54022 0000113E 2EFF2E[2311]            	jmp	far [cs:DosRetAddr23]	; jump back to DOS
 54023                                  
 54024                                  
 54025                                  	; Execute int 24h from low memory
 54026                                  LowInt24:
 54027                                  					; save the return address that is on
 54028                                  					; the stack
 54029 00001143 2E8F06[2711]            	pop	word [cs:DosRetAddr24]
 54030 00001148 2E8F06[2911]            	pop	word [cs:DosRetAddr24+2]
 54031                                  
 54032 0000114D CD24                    	int	24h			; crit error
 54033                                  					; turn on A20 it has been turned OFF
 54034                                  					; by int 28/23/24 handler.
 54035                                  
 54036 0000114F E85D00                  	call	EnsureA20ON		; M011: we must turn on A20 if OFF
 54037                                  
 54038 00001152 2EFF2E[2711]            	jmp	far [cs:DosRetAddr24]	; jump back to DOS
 54039                                  
 54040                                  
 54041                                   
 54042                                  	; Execute int 28h from low memory
 54043                                  LowInt28:
 54044 00001157 CD28                    	int	28h			; idle int
 54045                                  					; turn on A20 it has been turned OFF
 54046                                  					; by int 28/23/24 handler.
 54047                                  
 54048 00001159 E85300                  	call	EnsureA20ON		; M011: we must turn on A20 if OFF
 54049                                  
 54050 0000115C CB                      	retf
 54051                                  
 54052                                  ; DOSDATA:1115h (MSDOS 6.21, MSDOS.SYS)
 54053                                  
 54054                                  ;-------------------------------------------------------------------------
 54055                                  ;
 54056                                  ; int 21 ah=4b (exec) call will jump to the following label before xferring
 54057                                  ; control to the exec'd program. We turn off A20 inorder to allow programs
 54058                                  ; that have been packed by the faulty exepack utility to unpack correctly.
 54059                                  ; This is so because exepac'd programs rely on address wrap.
 54060                                  ;
 54061                                  ;------------------------------------------------------------------------- 
 54062                                  
 54063                                  disa20_xfer:
 54064 0000115D E83F00                  	call	XMMDisableA20		; disable A20
 54065                                  
 54066                                  	; Look at msproc.asm at label exec_go for understanding the following:
 54067                                  
 54068                                  	; DS:SI points to entry point
 54069                                  	; AX:DI points to initial stack
 54070                                  	; DX has PDB pointer
 54071                                  	; BX has initial AX value
 54072                                  
 54073 00001160 FA                      	cli
 54074 00001161 2EC606[2103]00          	mov	byte [cs:INDOS],0	; SS Override
 54075                                  
 54076 00001167 8ED0                    	mov	SS,AX			; set up user's stack
 54077 00001169 89FC                    	mov	SP,DI			; and SP
 54078 0000116B FB                      	sti
 54079                                  
 54080 0000116C 1E                      	push	DS			; fake long call to entry
 54081 0000116D 56                      	push	SI
 54082 0000116E 8EC2                    	mov	ES,DX			; set up proper seg registers
 54083 00001170 8EDA                    	mov	DS,DX
 54084 00001172 89D8                    	mov	AX,BX			; set up proper AX
 54085 00001174 CB                      	retf
 54086                                  
 54087                                  ;-------------------------------------------------------------------------
 54088                                  ;
 54089                                  ; M003:
 54090                                  ;
 54091                                  ; If an int 21 ah=25 call is made immediately after an exec call, DOS will
 54092                                  ; come here, turn A20 OFF restore user stack and registers before returning
 54093                                  ; to user. This is done in dos\msdisp.asm. This has been done to support 
 54094                                  ; programs compiled with MS PASCAL 3.2. See under TAG M003 in DOSSYM.INC for
 54095                                  ; more info.	
 54096                                  ;
 54097                                  ; Also at this point DS is DOSDATA. So we can assume DS DOSDATA. Note that
 54098                                  ; SS is also DOS stack. It is important that we do the XMS call on DOS's
 54099                                  ; stack to avoid additional stack overhead for the user.
 54100                                  ;
 54101                                  ; -------------------------------------------------------------------------
 54102                                  
 54103                                  disa20_iret:
 54104 00001175 E82700                  	call	XMMDisableA20
 54105 00001178 FE0E[2103]              	dec	byte [INDOS]
 54106 0000117C 8E16[8605]              	mov	SS,[USER_SS]		; restore user stack
 54107 00001180 8B26[8405]              	mov	SP,[USER_SP]
 54108 00001184 89E5                    	mov	BP,SP
 54109                                  	;mov	[BP+user_env.user_AX],AL
 54110 00001186 884600                  	mov	[bp],al
 54111 00001189 A1[F205]                	mov	AX,[NSP]
 54112 0000118C A3[8405]                	mov	[USER_SP],AX
 54113 0000118F A1[F005]                	mov	AX,[NSS]
 54114 00001192 A3[8605]                	mov	[USER_SS],AX
 54115                                  
 54116 00001195 58                      	pop	AX			; restore user regs
 54117 00001196 5B                      	pop	BX
 54118 00001197 59                      	pop	CX
 54119 00001198 5A                      	pop	DX
 54120 00001199 5E                      	pop	SI
 54121 0000119A 5F                      	pop	DI
 54122 0000119B 5D                      	pop	BP
 54123 0000119C 1F                      	pop	DS
 54124 0000119D 07                      	pop	ES
 54125 0000119E CF                      	iret
