 54188                                  	;les	di,[cs:HighMemory]	; with the 4 at FFFF:0090
 54189                                  
 54190 000011B5 31FF                    	xor	di,di
 54191 000011B7 8EC7                    	mov	es,di
 54192 000011B9 4F                      	dec	di
 54193 000011BA BE9000                  	mov	si,90h	; 0FFFFh:0090h	; HighMemory
 54194 000011BD 8EDF                    	mov	ds,di
 54195 000011BF BF8000                  	mov	di,80h	; 0000h:0080h	; LowMemory
 54196                                  	
 54197 000011C2 B90400                  	mov	cx,4
 54198 000011C5 FC                      	cld
 54199 000011C6 F3A7                    	repe    cmpsw
